class r{url;constructor(t){this.url=t}async updateSynonyms(t){return(await fetch(`${this.url}/`,{method:"POST",body:JSON.stringify(t),headers:{"Content-Type":"application/json"}})).json()}async getSynonyms(){return(await fetch(`${this.url}`)).json()}async saveDocument(t,n,s){return(await fetch(`${this.url}/${t}`,{method:"POST",body:JSON.stringify({id:n,text:s}),headers:{"Content-Type":"application/json"}})).json()}async updateDocument(t,n,s){return(await fetch(`${this.url}/${t}/${n}`,{method:"PUT",body:JSON.stringify({text:s}),headers:{"Content-Type":"application/json"}})).json()}async deleteDocument(t,n){return(await fetch(`${this.url}/${t}/${n}`,{method:"DELETE",headers:{"Content-Type":"application/json"}})).json()}async searchDocuments(t,n,s=1,e=10){return(await fetch(`${this.url}/${t}?search=${encodeURIComponent(n)}&page=${s}&size=${e}`)).json()}}var a=r;export{a as default};
